import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';


function Favorites(props) {
    // const favorites = []
    // let favoritesData = []
    let location = '215854'
    let data = []
    // const fetchFavoritesCities = () => {
    //     if (localStorage.favorites) {
    //         favorites = JSON.parse(localStorage.getItem('favorites'));
    //         debugger;
    //     }
    // }
    // useEffect(() => {
    //     fetchFavoritesCities()
    //     debugger;
    // }, [])

    const favoritesFromLocal = localStorage['favorites'] ? JSON.parse(localStorage['favorites']) : [];

    return (
        <div>
            <Menu />
            <h1>FAVORITES</h1>
            {
                favoritesFromLocal.map((fav) => (
                    location = fav.cityKey,
                    props.get_from_axios(location),
                    data = props.currentWeather,
                    props.pushToFavorites(data)
                )),

                <Grid container justifyContent="center" spacing={spacing}>
                {props.favorites.map((fav) => (
                  <Grid>
                    <Paper className={`${classes.paper} weatherToNextDay`}>
                      {/* <span>date: {props.weatherForFive.Headline.EffectiveDate}</span> */}
                      {/* {console.log(props.weatherForFive)} */}
                      {/* {console.log(days[(new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))).getDay()])} */}
                      {/* <span>{((new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))))}</span> */}
                      {/* <span className="day">{days[(new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))).getDay()]}     </span> */}
                      <span className="c">Min: {fav.Temperature?.Metric.Value}</span>
                      {/* <span className="max">Max: {props.weatherForFive.DailyForecasts[value].Temperature?.Maximum.Value}</span> */}
                    </Paper>
      
                  </Grid>
                ))}
              </Grid>
                // 
                // <WeatherForFavorite cityKey={'45253'} cityName='Teresina' />
            }
        </div>
        // 
    )
}
const mapStateToProps = state => {
    return {
        favorites: state.favorites,
        currentWeather: state.currentWeather,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        pushToFavorites: (favFromLocal) => dispatch(pushToFavorites(favFromLocal)),
        get_from_axios: (location) => dispatch(get_from_axios(location)),
    }
    // return {get_from_axios: (cur) => dispatch(get_from_axios(cur)) }
}
export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
