import React, { useState, useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';
import FavoriteIcon from '@material-ui/icons/Favorite';
import IconButton from '@material-ui/core/IconButton';
import { telAvivContext } from './TelAvivContext'
import { forFiveTelAvivContext } from './ForFiveTelAvivContext'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
    },
    control: {
        padding: theme.spacing(2),
    },
}));

export default function WeatherToday(props) {
    const classes = useStyles();
    return (
        <Grid container justifyContent="center" >
            <span className="cityName">{props.cityName}</span>
            <Paper className={`${classes.paper} today`}>
                <span>date: {props.weatherToday.LocalObservationDateTime?.substring(0, 10)}</span>
                <span> C: {props.weatherToday.Temperature?.Metric.Value}</span>
            </Paper>
        </Grid>)
}
