import React, { createContext, useEffect, useState, useContext } from 'react';
// import AutomaticCities from './AutomaticCities';
import { connect } from 'react-redux';
import { get_from_axios } from './actions'


function Favorites(currentWeather, get_from_axios) {
    let favorites = []
    const fetchFavoritesCities = () => {
        if (localStorage.favorites) {
            favorites = JSON.parse(localStorage.getItem('favorites'));
        }
    }
    return (
        <div>
            {favorites ? favorites.map(fav => {
                get_from_axios(fav.cityKey);

            }) : <h1>no favorites cities!</h1>
            }
        </div>
    )
}
const mapStateToProps = state => {
    return {
        currentWeather: state.currentWeather,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        get_from_axios: (location) => dispatch(get_from_axios(location)),
    }
    // return {get_from_axios: (cur) => dispatch(get_from_axios(cur)) }
}
export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
