import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';

export const citiesContext = createContext([]);

export default function AutocompleteSearchContext(props) {
    const [cities, setCities] = useState([]);
    useEffect(() => {
        axios.get("http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&q=Je&language=en-us")
        .then((data) => {
          setCities(data.data);
          debugger;
        })
    }, []);

    return (
      <AutocompleteSearchContext.Provider value={cities}>
        { props.children }
      </AutocompleteSearchContext.Provider>
    )
}
