// export default getReducer

// import dailyForecastsInitial from '../../public/dailyForecasts.json'
const initialState = {
  // ToDoList: [],
  // selectedToDo: null,
  // showAddToDo: false
  autocompleteResults: '',
  favorites: [],
  currentWeather: {},
  dailyForecasts: {}
}

export const weatherReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CURRENT_WEATHER':

      return {
        ...state,
        currentWeather: action.payload

      }
    case 'FIVE_DAILY_FORECASTS':

      return {
        ...state,
        dailyForecasts: action.payload

      }
    case 'AUTOCOMPLETE_SEARCH':

      return {
        ...state,
        autocompleteResults: action.payload

      }
    case 'PUSH_TO_FAVORITES':
      let arr = state.favorites
      arr.push(action.payload)
      return {
        ...state,
        favorites: arr

      }

  }
  return state;
}
export default weatherReducer;
