import axios from 'axios';

export const getAtutocomplete = (txt) => {
    return (dispatch) => {
        debugger;
        axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&q=${txt}&language=en-us`)
            .then((data) => {
                dispatch(autocomplete(data.data))

            })
    }
}
export const get_from_axios = () => {
    return (dispatch) => {
        debugger
        // axios.get(`http://dataservice.accuweather.com/currentconditions/v1/215854?apikey=TL640YqJDj7rGMdcG6nIsfoo9SrjZfoZ`)
        // axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${cur}?apikey=TL640YqJDj7rGMdcG6nIsfoo9SrjZfoZ`)
        axios.get('/currentWeatherRespose.json')
            .then((response) => {
                debugger
                dispatch(getCurrentWeather(response.data[0]))
                console.log(response.data)
                console.log(response.data[0])
                console.log(response.data[0].WeatherText)
            })
            .catch(err => {
                console.log("have in get_from_axios problem :(!!!")
            })
    };
}

export const getTodayWeather = (key) => {
    return (dispatch) => {
        debugger;
        axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${key}?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf`)
            .then((data) => {
                dispatch(todayWeather(data.data))
            })
    }
}

export const getTodayTelAviv = (key) => {
    return (dispatch) => {
        debugger;
        axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${215845}?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf`)
            .then((data) => {
                dispatch(todayWeather(data.data))
            })
    }
}

export const getForFiveWeather = (key) => {
    return (dispatch) => {
        debugger;
        axios.get(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${key}?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&metric=true`)
            .then((data) => {
                dispatch(weatherForFive(data.data))

            })
    }
}

export const autocomplete = (data) => {
    return {
        type: 'location_autocomplete',
        payload: data
    }
}
export const todayWeather = (data) => {
    return {
        type: 'today_weather',
        payload: data
    }
}
export const weatherForFive = (data) => {
    return {
        type: 'for_five_weather',
        payload: data
    }
}
