import axios from 'axios';


export const get_from_axios = (key) => {
    return (dispatch) => {
        debugger;
        axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${key}?apikey=6nwj4hRlQs081XAALVnPps900o0ALHiY`)
            // axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${cur}?apikey=TL640YqJDj7rGMdcG6nIsfoo9SrjZfoZ`)
            // axios.get('/p.json')
            .then((response) => {
                debugger
                dispatch(getCurrentWeather(response.data[0]))
                console.log(response.data)
                console.log(response.data[0])
                console.log(response.data[0].WeatherText)
            })
            .catch(err => {
                console.log("have in get_from_axios problem :(!!!")
            })
    };
}

export const fetchDailyForecast = (key) => {
    return (dispatch) => {
        debugger
        axios.get(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${key}?apikey=6nwj4hRlQs081XAALVnPps900o0ALHiY&metric=true`)//key 2 
        // axios.get(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${key}?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&metric=true`)//good from server!
        // axios.get('s.json') // good from json!!
            .then((data) => {
                debugger
                dispatch(fiveDailyForecastsAction(data.data))
                console.log(data.data)
            })
            .catch(err => {
                console.log("have in fetchDailyForecast problem :(!!!")
            })
    };
}

export const fetchSearchResult = (text) => {
    return (dispatch) => {
        debugger
        axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=6nwj4hRlQs081XAALVnPps900o0ALHiY&q=${text}&language=en-us`)//מפתח של רותי
        // axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&q=${text}&language=en-us`)//good from server!
        // axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${locationKey}?apikey=TL640YqJDj7rGMdcG6nIsfoo9SrjZfoZ`)
        // axios.get('/a.json') // good from json!!
            .then((response) => {
                debugger
                dispatch(autocompleteSearchAction(response.data))
                console.log(response.data)
            })
            .catch(err => {
                console.log("have in fetchSearchResult problem :(!!!")
            })
    };
}


export const getCurrentWeather = (data) => {
    return {
        type: 'CURRENT_WEATHER',
        payload: data
    }
}

export const fiveDailyForecastsAction = (data) => {
    return {
        type: 'FIVE_DAILY_FORECASTS',
        payload: data
    }
}

export const autocompleteSearchAction = (data) => {
    return {
        type: 'AUTOCOMPLETE_SEARCH',
        payload: data
    }
}
