import axios from 'axios';
import { useDispatch } from 'react-redux'
function geAtutocomplete(txt) {
    axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=uhtrRSo6CDxLgAFlBtnEZh2wWkRANgZf&q=${txt}&language=en-us`)
        .then((data) => {
            dispatch(autocomplete(data.data))

        })
}

export const autocomplete = (data) => {
    return {
        type: 'location_autocomplete',
        payload: data
    }
}
