import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';

export const autocompleteSearchContext = createContext([]);


export default function AutocompleteSearchContext(props) {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    axios.get('/product.json')
      .then((data) => {
        debugger;
        setProducts(data.data);
      })
  }, []);
  
  return (
    <autocompleteSearchContext.Provider value={products}>
      { props.children }
    </autocompleteSearchContext.Provider>
  )
}
