const initialState = { 
    automatic:[],
    currentWeather:[],
    forFiveWeather: {}
}

const getReducer = (state = initialState, action) => {
    switch (action.type) {
        // case 'for_five': return{

        // }
        // case 'weather_today': return{
        // }
        case 'location_autocomplete': return {
            ...state,
            // automatic: action.payload
            automatic: action.payload

        }
        case 'today_weather': return {
            ...state,
            // automatic: action.payload
            currentWeather: action.payload

        }

        case 'for_five_weather': return {
            ...state,
            // automatic: action.payload
            forFiveWeather: action.payload

        }
    }
    return state;

}
export default getReducer
