import React, { createContext, useEffect, useState, useContext } from 'react';
// import AutomaticCities from './AutomaticCities';
import { connect } from 'react-redux';
import { get_from_axios } from './actions'
import WeatherToday from './WeatherToday'
import Try from './Try'
import WeatherForFavorite from './WeatherForFavorite';
import Menu from './Menu'
import { pushToFavorites } from './actions'


function Favorites(props) {
    // const favorites = []
    // let favoritesData = []
    let location = '215854'
    // const fetchFavoritesCities = () => {
    //     if (localStorage.favorites) {
    //         favorites = JSON.parse(localStorage.getItem('favorites'));
    //         debugger;
    //     }
    // }
    // useEffect(() => {
    //     fetchFavoritesCities()
    //     debugger;
    // }, [])

    const favoritesFromLocal = localStorage['favorites'] ? JSON.parse(localStorage['favorites']) : [];

    return (
        <div>
            <Menu />
            <h1>FAVORITES</h1>
            {
                favoritesFromLocal.map((fav) => (
                    <WeatherForFavorite cityKey={fav.cityKey} cityName={fav.cityName} />
                ))
                // 
                // <WeatherForFavorite cityKey={'45253'} cityName='Teresina' />
            }
        </div>
    )
}
const mapStateToProps = state => {
    return {
        favorites: state.favorites,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        pushToFavorites: (fav) => dispatch(pushToFavorites(fav)),
    }
    // return {get_from_axios: (cur) => dispatch(get_from_axios(cur)) }
}
export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
