import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';

export const telAvivContext = createContext([]);


export default function TelAvivContext(props) {
    const [weatherToday, setWeatherToday] = useState([]);
    // const [degreesForFiveDays, setDegreesForFiveDays] = useState([21, 22, 23, 24, 25]);
    useEffect(() => {
        axios.get('/p.json')
            .then((data) => {
                debugger;
                setWeatherToday(data.data);
                debugger;
            })
    }, []);
    return (
        <telAvivContext.Provider value={weatherToday}>
            {props.children}
        </telAvivContext.Provider>
    )
}
