import React, { useState, useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';
import FavoriteIcon from '@material-ui/icons/Favorite';
import IconButton from '@material-ui/core/IconButton';
import { telAvivContext } from './TelAvivContext'
import { forFiveTelAvivContext } from './ForFiveTelAvivContext'


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));

export default function SpacingGrid(props) {
  // const w = useContext(forFiveTelAvivContext);
  // const [weatherDays, setWeatherDays] = useState([]);
  // const w = useContext(telAvivContext);
  // const [weatherToday, setWeatherToday] = useState([]);
  // const [degreesToday, setDegreesToday] = useState([23.5]);
  // const [city, setCity] = useState('Tel Aviv');
  // const [degreesForFiveDays, setDegreesForFiveDays] = useState([21, 22, 23, 24, 25]);
  const [spacing, setSpacing] = React.useState(7);
  // שליי
  // const [degreesToday, setDegreesToday] = useState();
  // 
  const classes = useStyles();
  var days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  // useEffect(() => {
  //   setWeatherToday(w)
  // }, [w]);
  // useEffect(() => {
  //   setWeatherDays(w)
  // }, [w])
  const handleChange = (event) => {
    setSpacing(Number(event.target.value));
  };

  return (
    <Grid container className={classes.root} spacing={2}>
      {console.log(props)}
      {/* {console.log(weatherDays)} */}
      {/* {weatherToday? console.log(weatherToday[0]): ''} */}
      {/* {console.log(weatherToday[0]['Temperature'])} */}
      <Grid item xs={12}>
        {/* <Grid container justifyContent="center" >
          <span className="cityName">{props.cityName}</span>
          <Paper className={`${classes.paper} today`}>
            <span>date: {props.weatherToday.LocalObservationDateTime?.substring(0, 10)}</span>
            <span> C: {props.weatherToday.Temperature?.Metric.Value}</span>
          </Paper>
          <IconButton className="addToFavorite" aria-label="favorite">
            <FavoriteIcon />
          </IconButton>
        </Grid> */}
        <Grid container justifyContent="center" spacing={spacing}>
          {[0, 1, 2, 3, 4].map((value) => (
            <Grid key={value} item>
              <Paper className={`${classes.paper} weatherToNextDay`}>
                {/* <span>date: {props.weatherForFive.Headline.EffectiveDate}</span> */}
                {console.log(props.weatherForFive)}
                {console.log(days[(new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))).getDay()])}
                {/* <span>{((new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))))}</span> */}
                <span className="day">{days[(new Date(props.weatherForFive.DailyForecasts[value].Date.substring(0, 10))).getDay()]}     </span>
                <span className="min">Min: {props.weatherForFive.DailyForecasts[value].Temperature?.Minimum.Value}</span>
                <span className="max">Max: {props.weatherForFive.DailyForecasts[value].Temperature?.Maximum.Value}</span>
              </Paper>

            </Grid>
          ))}
        </Grid>
      </Grid>
    </Grid>

  );
}
