import React, { createContext, useEffect, useState, useContext } from 'react';
import Search from './Search';
import WeatherView from './WeatherView';
import TelAvivContext from './TelAvivContext';
import { get_from_axios, fetchDailyForecast, fetchSearchResult } from './actions'
import { connect } from 'react-redux';
import WeatherForFive from './SpacingGrid'


function Home({ currentWeather, get_from_axios, dailyForecasts, fetchDailyForecast, autocompleteResults, fetchSearchResult }) {
    let location = '215854'
    let text = ""
    // let txtToSearch = ''
    const [textToSearch, setTextToSearch] = useState("")
    const getAxios = (e) => {
        text = e
        fetchSearchResult(text)
    }

    const getContent = (e) =>{
        debugger
        console.log(e)
    }
    // useEffect(() => {
    //     debugger;
    //     console.log(textToSearch)
    //     fetchSearchResult(textToSearch)
    // }, textToSearch)

    useEffect(() => {
        get_from_axios(location);
        let num = 215854;
        fetchDailyForecast(location);//num

    }, [])
    return (
        <div>
            {/* <Search /> */}

            <input type="text" list="browsers" onmouseleave={(e) => {getContent(e.target.value)}} onChange={(e) => { getAxios(e.target.value) }} />
            {console.log(textToSearch)}
            {console.log(fetchSearchResult)}
            <datalist id="browsers">
                {autocompleteResults? autocompleteResults.map(city =>
                (
                    <option>{city.LocalizedName}</option>
                    // <option value={city.Key}>{city.LocalizedName}</option>
                )): <div>no data!</div>}
            </datalist>

            {console.log(currentWeather)}
            {/* <TelAvivContext> */}
            <WeatherForFive weatherToday={currentWeather} weatherForFive={dailyForecasts} />
            {/* </TelAvivContext> */}

        </div>
    )

}

const mapStateToProps = state => {
    return {
        currentWeather: state.currentWeather,
        dailyForecasts: state.dailyForecasts,
        autocompleteResults: state.autocompleteResults
    }
}

const mapDispatchToProps = dispatch => {
    return {
        get_from_axios: (location) => dispatch(get_from_axios(location)),
        fetchDailyForecast: (location) => dispatch(fetchDailyForecast(location)),
        fetchSearchResult: (text) => dispatch(fetchSearchResult(text))
    }
    // return {get_from_axios: (cur) => dispatch(get_from_axios(cur)) }
}
export default connect(mapStateToProps, mapDispatchToProps)(Home);
