import React, { useState, useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { get_from_axios } from './actions';
import { connect } from 'react-redux';


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
    },
    control: {
        padding: theme.spacing(2),
    },
}));

function WeatherForFavorite(props) {
    let location = props.cityKey
    const classes = useStyles();
    debugger;
    console.log(props)
    
    useEffect(() => {
        props.get_from_axios(location)
        debugger;
    })

    return (
        <Grid container justifyContent="center" >
            <div className="girdToday">
            <Paper className={`${classes.paper} today`}>
                <span>{props.cityName}</span>
                    <span className="todayInToday">Today</span>
                    {/* <span>date: </span> */}
                    <span>{props.currentWeather?.LocalObservationDateTime?.substring(0, 10)}</span>
                    <span className="todayDegree">
                        <span className="degreesSpan"> C:</span>
                        {props.currentWeather?.Temperature?.Metric.Value}</span>


                </Paper>
            </div>
        </Grid>)
}
const mapStateToProps = state => {
    return {
        currentWeather: state.currentWeather,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        get_from_axios: (location) => dispatch(get_from_axios(location)),
    }
    // return {get_from_axios: (cur) => dispatch(get_from_axios(cur)) }
}
export default connect(mapStateToProps, mapDispatchToProps)(WeatherForFavorite);
